import React from "react";

const EmotionItem = ({ emotion_id, emotion_img, emotion_descript, handleClickEmote, isSelected }) => {
    return (
        <div onClick={() => handleClickEmote(emotion_id)} className={["EmotionItem", isSelected ? `EmotionItem_on_${emotion_id}` : `EmotionItem_off`]}>
            <img src={emotion_img} />
            <span>{emotion_descript}</span>
        </div>
    );
};

export default EmotionItem;
