import { createSlice } from "@reduxjs/toolkit";

let id = 1;
const write = createSlice({
    name: "write",

    initialState: {
        writeList: [
            {
                id: 0,
                date: 1654239638873,
                content: "나는 조주선입니다.",
                emotion: 5,
            },
        ],
    },

    reducers: {
        create: (state, action) => {
            return {
                ...state,
                writeList: state.writeList.concat({
                    id: id++,
                    date: new Date(action.payload.date).getTime(),
                    content: action.payload.content,
                    emotion: action.payload.emotion,
                }),
            };
        },
        update: (state, action) => {
            return {
                ...state,
                writeList: {
                    date: new Date(action.payload.date).getTime(),
                    content: action.payload.content,
                    emotion: action.payload.emotion,
                },
            };
        },

        remove: (state, action) => {
            return {
                ...state,
                writeList: state.writeList.filter((it) => it.id !== action.payload),
            };
        },
    },
});

export const { create, remove, update } = write.actions;

console.log(write);
console.dir(write);

export default write.reducer;
